select * from RULE_engine


		and rule_engine_id = #{rule_engine_id}


		and rule_base_type = #{rule_base_type}


		and city_id = #{city_id}


		and company_id = #{company_id}


		and rule_base_name = #{rule_base_name}


		and rule_base_content = #{rule_base_content}


		and rule_base_postil = #{rule_base_postil}


		and status = #{status}


		and last_updated = #{last_updated}


		and metadata_item = #{metadata_item}

