select * from INSBOrderlistenpush


		and platformtype = #{platformtype}


		and operationtype = #{operationtype}


		and taskid = #{taskid}


		and taskcode = #{taskcode}


		and status = #{status}


		and message = #{message}


		and lzgid = #{lzgid}


		and taskname = #{taskname}


		and subtaskid = #{subtaskid}

select count(1) from INSBOrderlistenpush


		and platformtype = #{platformtype}


		and operationtype = #{operationtype}


		and taskid = #{taskid}


		and taskcode = #{taskcode}


		and status = #{status}


		and message = #{message}


		and lzgid = #{lzgid}


		and taskname = #{taskname}


		and subtaskid = #{subtaskid}

select * from INSBOrderlistenpush


		and platformtype = #{platformtype}


		and operationtype = #{operationtype}


		and taskid = #{taskid}


		and taskcode = #{taskcode}


		and status = #{status}


		and message = #{message}


		and lzgid = #{lzgid}


		and taskname = #{taskname}


		and subtaskid = #{subtaskid}


LIMIT 2

select * FROM insborderlistenpush o 


		AND o.taskid = #{taskid}


		AND o.taskcode = #{taskcode}


		AND o.operationtype = #{operationtype}


		AND o.lzgid = #{lzgid}


ORDER BY o.createtime  LIMIT 1
