mapper.select:
select * from INSBWorktimearea


		and operater = #{operater}


		and insbworktimeid = #{insbworktimeid}


		and workdaystart = #{workdaystart}


		and workdayend = #{workdayend}


		and daytimestart = #{daytimestart}


		and daytimeend = #{daytimeend}


		and isremindtime = #{isremindtime}

select * from INSBWorktimearea


		and operater = #{operater}


		and insbworktimeid = #{insbworktimeid}


		and workdaystart = #{workdaystart}


		and workdayend = #{workdayend}


		and daytimestart = #{daytimestart}


		and daytimeend = #{daytimeend}


		and isremindtime = #{isremindtime}


limit 2

select count(1) from INSBWorktimearea


		and operater = #{operater}


		and insbworktimeid = #{insbworktimeid}


		and workdaystart = #{workdaystart}


		and workdayend = #{workdayend}


		and daytimestart = #{daytimestart}


		and daytimeend = #{daytimeend}


