mapper.select:
select id,prizename,prizetype,counts,effectivedays,status,IFNULL(noti,'') AS noti,autouse,
date_format(effectivetime,'%Y-%m-%d') AS effectivetime,
date_format(terminaltime,'%Y-%m-%d') AS terminaltime,
date_format(createtime,'%Y-%m-%d %H:%i:%s') AS createtime
from INSBMarketPrize


		and prizename = #{prizename}


		and prizetype = #{prizetype}


		and date_format(effectivetime,'%Y-%m-%d') >= date_format(#{effectivetime},'%Y-%m-%d')


		and date_format(terminaltime,'%Y-%m-%d') <= date_format(#{terminaltime},'%Y-%m-%d')


		and status = #{status}


		and counts = #{counts}


ORDER BY

	${sort} ${order}


	createtime desc


	LIMIT #{offset},#{limit}

select count(1) from INSBMarketPrize


		and prizename = #{prizename}


		and prizetype = #{prizetype}


		and date_format(effectivetime,'%Y-%m-%d') >= date_format(#{effectivetime},'%Y-%m-%d')


		and date_format(terminaltime,'%Y-%m-%d') <= date_format(#{terminaltime},'%Y-%m-%d')


		and status = #{status}


		and autouse = #{autouse}


		and counts = #{counts}

select * from INSBMarketPrize


		and prizename = #{prizename}


		and prizetype = #{prizetype}


		and counts = #{counts}


		and effectivetime = #{effectivetime}


		and terminaltime = #{terminaltime}


		and effectivedays = #{effectivedays}


		and status = #{status}


		and autouse = #{autouse}


LIMIT 2
