mapper.select:
select * from INSBPayresult


		and status = #{status}


		and bizId = #{bizId}


		and insSerialNo = #{insSerialNo}


		and paySerialNo = #{paySerialNo}


		and amount = #{amount}


		and transDate = #{transDate}


		and payResult = #{payResult}


		and orderState = #{orderState}


		and payResultDesc = #{payResultDesc}


		and payflowno = #{payflowno}

