select brandname, FORMAT(newcarprice,1) newcarprice, FORMAT(newcartaxprice,1) newcartaxprice, FORMAT(analogyprice,1) analogyprice, FORMAT(analogytaxprice,1) analogytaxprice, listedyear, seat, unwrtweight, fullweight, displacement from INSBPlatcarprice
and taskid = #{taskid}
and companyid = #{companyid}
order by
${sort} ${order}
createtime desc
