SELECT b.id, b.paychannelname, b.paytype FROM insbagreementpaymethod a LEFT JOIN insbpaychannel b ON a.paychannelid = b.id LEFT JOIN insbchannelagreement e ON a.agreementid = e.id LEFT JOIN insbchannel g ON e.channelid = g.id LEFT JOIN insbagreementarea h ON e.id = h.agreementid WHERE g.channelinnercode = #{channelinnercode} AND a.providerid = #{providerid} AND h.city = #{city}
AND ( g.deleteflag != '0' OR g.deleteflag IS NULL ) AND e.agreementstatus = '1'
AND a.deptid = #{dept}

delete from INSBAgreementpaymethod 


		and agreementid = #{agreementid}


		and providerid = #{providerid}
