mapper.select:
select * from INSBBankcard


		and cardprefix = #{cardprefix}


		and cardname = #{cardname}


		and banktoname = #{banktoname}


		and cardtype = #{cardtype}


		and cardnumlength = #{cardnumlength}


		and cardbankdeptcode = #{cardbankdeptcode}


	limit #{offset},#{limit}

select @rownum:=@rownum+1 as rownum,
 	id, cardprefix,cardname,banktoname , CASE WHEN cardtype='01' THEN '贷记卡'   
		WHEN cardtype='02' THEN '借记卡'  
		WHEN cardtype='03' THEN '预付卡'
		WHEN cardtype='04' THEN '准货记卡'
		WHEN cardtype='05' THEN '其它'
		end  cardtype ,cardnumlength,cardbankdeptcode,operator ,createtime,modifytime,noti from INSBBankcard a,
		(select @rownum:=0) b


		and cardprefix = #{cardprefix}


		and cardname = #{cardname}


		and banktoname = #{banktoname}


		and cardtype = #{cardtype}


		and cardnumlength = #{cardnumlength}


		and cardbankdeptcode = #{cardbankdeptcode}


order by 

	  ${sort}  ${order}  


	 createtime desc 

	limit #{offset},#{limit}

select count(1) from INSBBankcard


		and cardprefix = #{cardprefix}


		and cardname = #{cardname}


		and banktoname = #{banktoname}


		and cardtype = #{cardtype}


		and cardnumlength = #{cardnumlength}


		and cardbankdeptcode = #{cardbankdeptcode}

select * from INSBBankcard


		and banktoname = #{banktoname}


	limit #{offset},#{limit}
