select count(1) from INSBRiskrenewalitem


		and riskid = #{riskid}


		and reitemname like CONCAT('%', #{reitemname},'%') 


		and istraffic = #{istraffic}


		and isbusiness = #{isbusiness}


		and istrafficandbusiness = #{istrafficandbusiness}


		and presentation = #{presentation}

SELECT id, operator, createtime, modifytime, noti, riskid, reitemname, (CASE WHEN c.istraffic='1' THEN '是' ELSE '否' END) istraffic, (CASE WHEN c.isbusiness='1' THEN '是' ELSE '否' END) isbusiness, (CASE WHEN c.istrafficandbusiness='1' THEN '是' ELSE '否' END) istrafficandbusiness, presentation FROM INSBRiskrenewalitem c
and riskid = #{riskid}
and reitemname like CONCAT('%', #{reitemname},'%')
and istraffic = #{istraffic}
and isbusiness = #{isbusiness}
and istrafficandbusiness = #{istrafficandbusiness}
and presentation = #{presentation}
